
library(grid)
library(vdiffr)


svg_text_abs <- '
<svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 200 100">

  <image href="data:image/png;base64," width="50" height="50" x="50" y="50" />

  <image href="data:image/png;base64," width="50" height="50" x="50" y="50" />

  <image href="data:image/png;base64," width="50" height="100" x="50" y="20" />


  <circle r="25" cx="50" cy="50" fill="transparent" stroke="blue" />
</svg>'


#~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~
# Saving SVG as I'll load it in firefox/chrome to compare rendering
#~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~
writeLines(svg_text_abs, "svg/test-image-absolute-size.svg")




svg_text_rel <- '
<svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 200 100">

  <image href="data:image/png;base64," width="15%" height="25%" x="50" y="50" />

  <circle r="25" cx="50" cy="50" fill="transparent" stroke="blue" />
</svg>'




svg_text_rel <- '
<svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 200 100">

  <image href="data:image/png;base64," width="15%" height="25%" x="50" y="50"/>

  <circle r="25" cx="50" cy="50" fill="transparent" stroke="blue" />
</svg>'

#~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~
# Saving SVG as I'll load it in firefox/chrome to compare rendering
#~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~
writeLines(svg_text_rel, "svg/test-image-relative-size.svg")



svg_text_transform <- '
<svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 200 100">

  <image href="data:image/png;base64," width="50" height="50" transform="rotate(10)"/>

  <circle r="25" cx="50" cy="50" fill="transparent" stroke="blue" />
</svg>'

#~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~
# Saving SVG as I'll load it in firefox/chrome to compare rendering
#~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~
writeLines(svg_text_transform, "svg/test-image-transform.svg")








if (FALSE) {
  svg <- read_svg(svg_text_abs); grid::grid.newpage(); grid::grid.draw(svg)

  svg <- read_svg(svg_text_rel); grid::grid.newpage(); grid::grid.draw(svg)


  svg <- read_svg(svg_text_transform); grid::grid.newpage(); grid::grid.draw(svg)
}



#~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~
# Example including rotaate text
#~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~
suppressMessages({
  test_that("<image> works", {
    g <- read_svg(svg_text_abs)
    vdiffr::expect_doppelganger("image-tag", g)
  })



  test_that("<image> works with relative sizing", {
    g <- read_svg(svg_text_rel)
    vdiffr::expect_doppelganger("image-rel", g)
  })

})




if (FALSE) {

  # generate some base64 test images.
  library(grid)
  library(openssl)

  grob <- rectGrob(gp = gpar(lwd = 1, col = 'red'))
  grid.newpage(); grid.draw(grob)

  N <- 10

  #~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~
  #~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~
  png("test-image-square.png", width = N, height = N)
  grob <- rectGrob(gp = gpar(lwd = 1, col = 'red'))
  grid.draw(grob)
  dev.off()

  bin <- readBin("test-image-square.png", what = raw(), n = 1e5)
  length(bin)
  cat(
    "data:image/png;base64,",
    openssl::base64_encode(bin),
    sep = ""
  )
  #~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~
  #~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~
  png("test-image-wide.png", width = N * 2, height = N)
  grob <- rectGrob(gp = gpar(lwd = 1, col = 'green'))
  grid.draw(grob)
  dev.off()

  bin <- readBin("test-image-wide.png", what = raw(), n = 1e5)
  length(bin)
  cat(
    "data:image/png;base64,",
    openssl::base64_encode(bin),
    sep = ""
  )


  #~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~
  #~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~
  png("test-image-tall.png", width = N, height = N * 2)
  grob <- rectGrob(gp = gpar(lwd = 1, col = 'blue'))
  grid.draw(grob)
  dev.off()

  bin <- readBin("test-image-tall.png", what = raw(), n = 1e5)
  length(bin)
  cat(
    "data:image/png;base64,",
    openssl::base64_encode(bin),
    sep = ""
  )


}



